package samplestring;

public class Mummy {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Mummy";
		int c=0;
		char k='m';
 for(int i=0;i<=s.length()-1;i++) {
	 if(s.charAt(i)==k)
		 c++;
 }
 System.out.println("The char m is printed: "+c+" times");
		

	}

}
